plugins {
    id("buildlogic.java-conventions")
}

dependencies {
    api(project(":pmd-core"))
    api(project(":pmd-cli"))
    api(project(":pmd-ant"))
    api(libs.net.sourceforge.pmd.pmd.designer)
    api(libs.org.slf4j.slf4j.simple)
    api(libs.org.apache.commons.commons.lang3)
    runtimeOnly(project(":pmd-languages-deps"))
    testImplementation(libs.org.junit.jupiter.junit.jupiter)
    testImplementation(libs.org.hamcrest.hamcrest)
    testImplementation(libs.org.apache.commons.commons.compress)
}

description = "PMD Distribution Packages"

java {
    withJavadocJar()
    sourceCompatibility = JavaVersion.VERSION_17
    targetCompatibility = JavaVersion.VERSION_17
    toolchain {
        languageVersion = JavaLanguageVersion.of(17)
    }
}